(3, 'Machanical engineering', 'All the books from Machanical engineering.', 'ME'),
(4, 'EEE', 'All the books from Electrical and Electronics Engineering Department.', 'EEE'),
(5, 'BBA', 'All the books from business administration.', 'BBA'),
